import React from 'react'
import { useRef } from 'react'
import useApi2 from '../hooks/use-api2'

function Test3() {
    const [result, setUrl] = useApi2()
    const urlRef = useRef()

    const getUrl = () =>{
        setUrl(urlRef.current.value)
        urlRef.current.value = ''
    }

  return (
    <div className='app'>
          <h1>Test3</h1>
          <div>
            <label>Url:
                <input type="text" ref={urlRef}/>
            </label>
            <button onClick={getUrl}>submit</button>
          </div>
          {result && <h3></h3>}
    </div>
  )
}

export default Test3
