import React, { useState } from 'react'
import { useEffect } from 'react'
import { useRef } from 'react'
import useApi2 from '../hooks/use-api2'

function Test4() {
    const [result, setUrl] = useApi2()
    const [content, setContent] = useState([])
    const urlRef = useRef()

    const getUrl = (url) => {
        setUrl(url.current.value)
        url.current.value = ''
    }


    useEffect(() => {
        console.log(result)

        if (result) {
            for (let key in result) {
                // content.push(result[key])
                setContent(prev=>{
                    return prev.concat(result[key])
                })
            }
        }
    }, [result])

    return (
        <div className='app'>
            <h1>Test3</h1>
            <div>
                <label>Url:
                    <input type="text" ref={urlRef} />
                </label>
                <button onClick={getUrl.bind(this, urlRef)}>submit</button>
            </div>
            {result && <h3>{content[0]}</h3>}
        </div>
    )
}

export default Test4
