import React from 'react'
import { useEffect } from 'react'
import { useRef } from 'react'
import useApi2 from '../hooks/use-api2'

function Test3() {
    const [result, setUrl] = useApi2()
    const urlRef = useRef()

    const getUrl = (url) =>{
        setUrl(url.current.value)
        url.current.value = ''
    }
    let content = []
    useEffect(()=>{
        if(result){
            for (let key in result) {
              content.push(result[key])
            }
        }

    },[result])

console.log(result)
  return (
    <div className='app'>
          <h1>Test3</h1>
          <div>
            <label>Url:
                <input type="text" ref={urlRef}/>
            </label>
            <button onClick={getUrl.bind(this, urlRef)}>submit</button>
          </div>
          {result && <h3>{content[0]}</h3>}
    </div>
  )
}

export default Test3
