import React, { useEffect, useState, useRef } from 'react'
import { useNavigate } from 'react-router-dom'

function CreateOne(props) {
    const nameRef = useRef()
    const addressRef = useRef()
    const [newPerson, setNewPerson] = useState('')
    const nav = useNavigate()

    useEffect(()=>{
        if(newPerson.name){
            props.setList(prev=>{
                return [...prev, newPerson]
            })
        }
    },[newPerson])
    console.log(props.list)
    const onSubmitHandler = (e) => {
        e.preventDefault()
        const name = nameRef.current.value
        const address = addressRef.current.value
        setNewPerson({ name, address, id: props.list.length })
        nameRef.current.value = ''
        addressRef.current.value = ''
    }
    return (
        <div className='app'>
            <h1>CreateOne</h1>
            <form onSubmit={onSubmitHandler}>
                <div>
                    <label>Name:
                        <input type="text" ref={nameRef}/>
                    </label>
                </div>
                <div>
                    <label>Address:
                        <input type="text" ref={addressRef}/>
                    </label>
                </div>
                <button type='submit' >Submit</button>
            </form>

           {newPerson && <div className='app'>
                <li>{newPerson.name}</li>
                <p>{newPerson.address}</p>
                <button onClick={nav.bind(this, `/seeAll/${newPerson.id}`)}>Learn more</button>
            </div>}

        </div>
    )
}

export default CreateOne
