import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import './App.css';
import Navbar from './components/navbar/Navbar';
import About from './components/pages/About';
import Create from './components/pages/Create';
import FindOne from './components/pages/FindOne';
import Home from './components/pages/Home';
import Index from './components/pages/Index';
import ShowOne from './components/pages/ShowOne';

const DUMMYDATA = [
  {name:"Wallie", age:31, id:0},
  {name:"Heshow", age:31, id:1},
  {name:"Meshia", age:33, id:2},
]
function App() {
  const [people, setPeople] = useState([])
  const [onePerson, setPerson] = useState('')
  useEffect(()=>{
    setPeople(DUMMYDATA)
  },[])
  console.log(people)
  return (
    <Router>
    <div className="app">
      <h1>Hi there!</h1>
    </div>
      <Navbar/>
      <Routes>
        <Route path={'/'} element={<Home />}/>
        <Route path={'/create'} element={<Create people={people} setPeople={setPeople} />}/>
        <Route path={'/findOne'} element={<FindOne people={people} />}/>
        <Route path={'/index'} element={<Index people={people} setPerson={setPerson} />}/>
        <Route path={'/about'} element={<About />}/>
        <Route path={'/index/:idx'} element={<Home />}/>
      </Routes>



      <About/>
      <ShowOne person={onePerson}/> 
    </Router>
  );
}

export default App;
