//     {path:'events/:id' ,element:<EventDetailPage/>},
//     {path:'events/new' ,element:<NewEventPage/>},
//     {path:'events/:id/edit' ,element:<EditEventPage/>}
//   ]},
// ])
function App() {
  return <RouterProvider router={router} />;
}

export default App;
